from PIL import Image
import pathlib
import requests
import json
from io import BytesIO
import base64

parent = pathlib.Path('D:\KOLEKSI\ANIME_NEW')
url = 'https://graphql.anilist.co'
query = 'query ($search: String){ Media(search: $search, type:ANIME) { id coverImage{extraLarge} title{romaji} }}'
ico_size = [(256,256)]

# region Top Image Base64
top64 = ''
# endregion

# region Bottom Image Base64
bottom64 = ''
# endregion

top_img = BytesIO(base64.b64decode(top64))
bottom_img = BytesIO(base64.b64decode(bottom64))

# exclude folder with name "[number]. xxx"
list_folder_parent = [x for x in parent.iterdir() if x.is_dir(
) and not x.name[0].isnumeric() and not x.name[1] == "."]


def getImg(title, path):
    vars = {"search": title}
    response = requests.post(
        url,
        json={"query": query, "variables": vars}
    )

    json_data = json.loads(response.text)
    img_url = json_data["data"]["Media"]["coverImage"]["extraLarge"]

    img_data = requests.get(img_url).content
    with open(pathlib.Path(path, 'icon.jpg'), 'wb') as handler:
        handler.write(img_data)

    return img_url


def icon_generator(top_img, img_target, bottom_img):
    images = [Image.open(x) for x in [top_img, img_target, bottom_img]]
    middle_img = images[1].resize((149, 211), Image.LANCZOS)
    new_img = Image.new("RGBA", (256, 256))
    new_img.paste(images[2])
    new_img.paste(middle_img, (31, 16))

    alphaComposite = Image.alpha_composite(new_img, images[0])
    alphaComposite.save(pathlib.Path(pathlib.Path(img_target).parent, "a.ico"), sizes = ico_size, bitmap_format="png")


for i in list_folder_parent:
    symbol = "◇"
    start= ""
    end = ""
    icon_jpg_path = pathlib.Path(i, "icon.jpg")
    icon_ico_path = pathlib.Path(i, "a.ico")

    if not icon_jpg_path.exists():
        symbol = "◈"
        start = '\033[1m';
        end = '\033[0m';
        getImg(i.name, i)
    
    icon_generator(top_img, icon_jpg_path, bottom_img)

    print(symbol," ",start+i.name+end)
    
